<?phpnamespace sts;use sts as core;if (!defined(__NAMESPACE__ . '\ROOT')) exit;if ($config->get('api_enabled')) {	if ($config->get('api_require_https')) {		if (!isset($_SERVER['HTTPS']) || ($_SERVER['HTTPS'] != 'on')) {			exit;		}	}	header('Cache-Control: no-cache, must-revalidate');	header('Content-Type: application/json; charset=utf-8');		$username 	= @$_GET['u'];	$key		= @$_GET['k'];	$action		= @$_GET['a'];		$json['username'] 		= $username;	$json['api_key']		= $key;	$json['api_version']	= 2;	$json['api_action']		= $action;	$data['data'] = json_encode($json);	$result = $api->receive($data);	$result = json_decode($result, true);	$return = array();			if ($result['success']) {			switch($action) {			case 'get_count_my_assigned_tickets':				$return['postfix']			= 'Assigned Tickets';				$return['data']['value']	= $result['tickets'];			break;			case 'get_count_open_tickets':				$return['postfix']			= 'Open Tickets';				$return['data']['value']	= $result['tickets'];			break;		}	}		echo json_encode($return);}?>